diff --git a/perf/O(n).js b/perf/O(n).js
new file mode 100644
index 0000000..5450e13
--- /dev/null
+++ b/perf/O(n).js
@@ -0,0 +1,20 @@
+'use strict';
+
+module.exports = function (str, len, ch) {
+  // convert `str` to `string`
+  str = str + '';
+
+  // doesn't need to pad
+  len = len - str.length;
+  if (len <= 0) return str;
+
+  // convert `ch` to `string`
+  if (!ch && ch !== 0) ch = ' ';
+  ch = ch + '';
+
+  while (len--) {
+    str = ch + str;
+  }
+
+  return str;
+}

