diff --git a/perf/es6Repeat.js b/perf/es6Repeat.js
new file mode 100644
index 0000000..40ebb19
--- /dev/null
+++ b/perf/es6Repeat.js
@@ -0,0 +1,19 @@
+'use strict';
+
+module.exports = function leftpadEs6Repeat (str, len, ch) {
+  var l;
+
+  str = String(str);
+  l = len - str.length;
+
+  if (!ch && ch !== 0) {
+    ch = ' ';
+  }
+
+  if (l > 0) {
+    return ch.repeat(l) + str;
+  }
+
+  return str;
+
+};

